CREATE TABLE "Tbl_Agencia" (
  "IdAgencia" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "NombAgencia" varchar(20),
  "Telefono" varchar(20)
);

CREATE TABLE "Tbl_Empresa_Transportes" (
  "IdEmpresaTrans" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "NombEmpresa" varchar(20),
  "Telefono" varchar(20),
  "MarcaVehi" varchar(20),
  "ColorVehi" varchar(20),
  "TipoVehiculo" varchar(20),
  "Placa" varchar(20),
  "SerieMotor" varchar(20),
  "IdConductor" integer
);

CREATE TABLE "Tbl_Conductor" (
  "IdConductor" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "Nombre" varchar(20),
  "Apellidos" varchar(20),
  "Direccion" varchar(20),
  "Telefono" varchar(20),
  "DNI" integer,
  "Licencia" varchar(20)
);

CREATE TABLE "Tbl_Cargo" (
  "IdCargo" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "NombCargo" varchar(20)
);

CREATE TABLE "Tbl_Tipo_Servicio" (
  "IdServicio" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "NombServicio" varchar(20),
  "Itinerario" varchar(20),
  "Cupos" int
);

CREATE TABLE "Tbl_Tipo_Pago" (
  "IdTipoPago" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "MedioPago" varchar(20),
  "Moneda" varchar(20),
  "TipoCambio" numeric
);

CREATE TABLE "Tbl_Administracion" (
  "IdTAdministracion" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "Usuario" varchar(20),
  "Password" varchar(20)
);

CREATE TABLE "Tbl_Guia" (
  "IdGuia" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "Usuario" varchar(20),
  "Password" varchar(20),
  "Nombre" varchar(20),
  "Apellidos" varchar(20),
  "Telefono" varchar(20),
  "DNI" integer,
  "IdEstadoGuia" integer
);

CREATE TABLE "Tbl_Estado_Gui" (
  "IdEstadoGuia" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "Nombre" varchar(20)
);

CREATE TABLE "Tbl_Cliente" (
  "IdCliente" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "Nombres" varchar(30),
  "Apellidos" varchar(50),
  "Pais" varchar(30),
  "Celular" varchar(13),
  "Email" varchar(50),
  "Password" varchar(20)
);

CREATE TABLE "Tbl_Empleado" (
  "IdEmpleado" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "Nombres" varchar(30),
  "Apellidos" varchar(50),
  "Pais" varchar(30),
  "Celular" varchar(13),
  "Email" varchar(50),
  "Usuario" varchar(30),
  "Password" varchar(20),
  "IdCargo" integer
);

CREATE TABLE "Tbl_Perfil_Ventas" (
  "IdPerfVentas" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "CodigoReserva" integer,
  "EstadoReserva" varchar(20),
  "Llegada" varchar(20),
  "Motivo" varchar(20)
);

CREATE TABLE "Tbl_Reserva_Internet" (
  "CodigoReserva" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "IdCliente" integer,
  "IdTipoPago" integer,
  "IdAgencia" integer,
  "IdServicio" integer,
  "IdEmpleado" integer,
  "IdGuia" integer,
  "Fecha" date,
  "Adultos" integer,
  "Children" integer,
  "Funcion" varchar(50),
  "Idioma" varchar(50),
  "Pais" varchar(30),
  "TotalaPagar" numeric,
  "PrecioPagado" numeric,
  "EstadodePago" varchar(30),
  "Email" varchar(30),
  "Celular" varchar(13),
  "Hospedaje" varchar(50),
  "Calicacion" integer,
  "Estado" boolean
);

ALTER TABLE "Tbl_Guia" ADD FOREIGN KEY ("IdEstadoGuia") REFERENCES "Tbl_Estado_Gui" ("IdEstadoGuia");

ALTER TABLE "Tbl_Reserva_Internet" ADD FOREIGN KEY ("IdGuia") REFERENCES "Tbl_Guia" ("IdGuia");

ALTER TABLE "Tbl_Empresa_Transportes" ADD FOREIGN KEY ("IdConductor") REFERENCES "Tbl_Conductor" ("IdConductor");

ALTER TABLE "Tbl_Perfil_Ventas" ADD FOREIGN KEY ("CodigoReserva") REFERENCES "Tbl_Reserva_Internet" ("CodigoReserva");

ALTER TABLE "Tbl_Reserva_Internet" ADD FOREIGN KEY ("IdEmpleado") REFERENCES "Tbl_Empleado" ("IdEmpleado");

ALTER TABLE "Tbl_Reserva_Internet" ADD FOREIGN KEY ("IdTipoPago") REFERENCES "Tbl_Tipo_Pago" ("IdTipoPago");

ALTER TABLE "Tbl_Reserva_Internet" ADD FOREIGN KEY ("IdCliente") REFERENCES "Tbl_Cliente" ("IdCliente");

ALTER TABLE "Tbl_Empleado" ADD FOREIGN KEY ("IdCargo") REFERENCES "Tbl_Cargo" ("IdCargo");

ALTER TABLE "Tbl_Reserva_Internet" ADD FOREIGN KEY ("IdServicio") REFERENCES "Tbl_Tipo_Servicio" ("IdServicio");

ALTER TABLE "Tbl_Reserva_Internet" ADD FOREIGN KEY ("IdAgencia") REFERENCES "Tbl_Agencia" ("IdAgencia");
